import React, { Component } from 'react';
import {connect} from "react-redux";
import {ButtonToolbar, ToggleButtonGroup, ToggleButton} from "react-bootstrap";



class FitnessFilters extends Component {

	render() {
		return (
			<div>
				<ButtonToolbar>
					<ToggleButtonGroup type="checkbox">
					<input onChange={this.props.handleOnChange} type="checkbox" name="cardio" />Cardio
					<input onChange={this.props.handleOnChange} type="checkbox" name="spin" />Spin
					<input onChange={this.props.handleOnChange} type="checkbox" name="pilates" />Pilates
					<input onChange={this.props.handleOnChange} type="checkbox" name="circuit_training" />Circuit Training
					<input onChange={this.props.handleOnChange} type="checkbox" name="kickboxing" />Kickboxing
					<input onChange={this.props.handleOnChange} type="checkbox" name="yoga" />Yoga
					<input onChange={this.props.handleOnChange} type="checkbox" name="boxing" />Boxing
					<input onChange={this.props.handleOnChange} type="checkbox" name="rowing" />Rowing
					<input onChange={this.props.handleOnChange} type="checkbox" name="barre" />Barre
					<input onChange={this.props.handleOnChange} type="checkbox" name="strength_training" />Strength Training
					</ToggleButtonGroup>
				</ButtonToolbar>
			</div>
		);
	}
}

function mapDispatchToProps(dispatch) {
	return {
		handleOnChange: (e) => dispatch(handleOnChange(e))
	}
}


function handleOnChange (e) {
		console.log(e.target.name)
		console.log(e.target.checked)
		if (e.target.checked) {
			return {type: "ADD_TO_FILTER", payload: e.target.name}
		}
		else {
			return {type: "REMOVE_FROM_FILTER", payload: e.target.name}
		}
	}

export default connect(null, mapDispatchToProps)(FitnessFilters);
