import React, { Component } from 'react';
import {connect} from "react-redux";
import {ButtonToolbar, ToggleButtonGroup, ToggleButton} from "react-bootstrap";



class FitnessFilters extends Component {

	render() {
		return (
			<div>
				<ButtonToolbar>
					<ToggleButtonGroup type="checkbox">
					<ToggleButton onChange={this.props.handleOnChange} name="cardio">Cardio</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="spin">Spin</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="pilates">Pilates</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="circuit_training">Circuit Training</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="kickboxing">Kickboxing</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="yoga">Yoga</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="boxing">Boxing</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="rowing">Rowing</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="barre">Barre</ToggleButton>
					<ToggleButton onChange={this.props.handleOnChange} name="strength_training">Strength Training</ToggleButton>
					</ToggleButtonGroup>
				</ButtonToolbar>
			</div>
		);
	}
}

function mapDispatchToProps(dispatch) {
	return {
		handleOnChange: (e) => dispatch(handleOnChange(e))
	}
}


function handleOnChange (e) {
		console.log(e.target.name)
		console.log(e.target.checked)
		if (e.target.checked) {
			return {type: "ADD_TO_FILTER", payload: e.target.name}
		}
		else {
			return {type: "REMOVE_FROM_FILTER", payload: e.target.name}
		}
	}

export default connect(null, mapDispatchToProps)(FitnessFilters);
