import React, { Component } from 'react';
import {connect} from "react-redux";
import * as sessionActions from "../actions/sessionActions.js"

export class Login extends Component {

  state = {
    credentials: {
      email: "",
      password: "",
    },
    currentAdmin: {}
  }

componentDidMount() {
  const token = localStorage.token;
  fetch("http://localhost:3000/api/v1/profile", {
    method: "GET", 
    headers: {
      Authorization: `Bearer ${token}`
    }
  })
      .then(resp => resp.json())
      .then(data => {
      if (!data.error) {
        this.props.currentAdmin(data)
      } else{
        console.log(data.error)
      }
    })
}

handleChange = (event) => {
  let field = event.target.name;
  let credentials = this.state.credentials
  credentials[field] = event.target.value;
  return this.setState({
    credentials: credentials
  })
}

handleSubmit = (event) => {
  event.preventDefault();
  fetch("http://localhost:3000/api/v1/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(this.state)
    })
      .then(resp => resp.json())
      .then(data => {
        if (!data.message) {
          localStorage.token = data.token;
        } else {
          alert(data.message)
        }
      })
  // this.props.actions.logInAdmin(this.state.credentials);
  }


  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div>
            <input type="text" name="email" onChange={this.handleChange} value={this.state.credentials.email} placeholder="Email Address" />
          </div>
          <div>
            <input type="password" name="password" onChange={this.handleChange} value={this.state.credentials.password} placeholder="Password" />
          </div>
          <div>
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    currentAdmin: (admin) => dispatch({type: "LOG_IN_SUCCESS", payload: admin}),
  }
}

export default connect(null, mapDispatchToProps)(Login);
