import React, { Component } from 'react';

class NewClassForm extends Component {

	state = {
		classes: []
	}

	handleChange = (event) => {
		this.setState({
			classes: event.target.value
		});
	}


	render() {
		return (
			<div>
				<h1>Enter a new class</h1>
				<form>
						<div><label>Class Name</label>
						<input name="name" type="text" onChange={this.handleChange} /></div>
						<div><label>Description</label>
						<input name="description" type="text" onChange={this.handleChange} /></div>
						<div><label>Drop-in Price</label>
						<input name="price" type="number" onChange={this.handleChange} /></div>
						<div><label>Class length</label>
						<input name="class_length" type="number" onChange={this.handleChange} /></div>
						<div><label>Date/Time</label>
						<input name="time" type="datetime-local" onChange={this.handleChange} /></div>
						<div><label>Select Class Type(s): </label></div>
						Cardio<input name="" type="checkbox" onChange={this.handleChange} />
						Cycling<input name="" type="checkbox" onChange={this.handleChange} />
						Pilates<input name="" type="checkbox" onChange={this.handleChange} />
						Yoga<input name="" type="checkbox" onChange={this.handleChange} />

					<div><input type="submit" /></div>
				</form>
			</div>
		);
	}
}

export default NewClassForm;
