export class Login extends Component {

  state = {
    credentials: {
      email: "",
      password: "",
    }
  }

componentDidMount() {
  const token = localStorage.token;
  fetch("http://localhost:3000/login", {
    method: "GET", 
    headers: {
      Authorization: `Bearer ${token}`
    }
  })
      .then(resp => resp.json())
      .then(data => {
      if (!data.error) {
        this.setState({
          credentials: data
        })
      }
    })
}

